  [&quot;Result (0): 2&quot;, &quot;Result (1): 1&quot;]
  [&quot;Result (0): 2&quot;, &quot;Result (1): 2&quot;]
  [&quot;Result (0): 3&quot;, &quot;Result (1): 2&quot;]
  [&quot;Result (0): 3&quot;, &quot;Result (1): 3&quot;]
  [&quot;Result (0): 4&quot;, &quot;Result (1): 3&quot;]
  [&quot;Result (0): 4&quot;, &quot;Result (1): 4&quot;]
*/
const subscribe = combined.subscribe(val =&gt; console.log(val));

