*/
const hello = Rx.Observable.create(function(observer) {
  observer.next(&apos;Hello&apos;);
  observer.next(&apos;World&apos;);
});

// &#x8F93;&#x51FA;: &apos;Hello&apos;...&apos;World&apos;
