// &#x6BCF;1&#x79D2;&#x53D1;&#x51FA;&#x503C;
const source = Rx.Observable.interval(1000);
// 5&#x79D2;&#x540E;&#x53D1;&#x51FA;&#x503C;
const timer = Rx.Observable.timer(5000);
// &#x5F53;5&#x79D2;&#x540E; timer &#x53D1;&#x51FA;&#x503C;&#x65F6;&#xFF0C; source &#x5219;&#x5B8C;&#x6210;
